import { TestBed } from '@angular/core/testing';
import { SvgEditorComponent } from './svg-editor.component';
describe('SvgEditorComponent', () => {
    let component;
    let fixture;
    let DomSanitizer;
    beforeEach(async () => {
        await TestBed.configureTestingModule({
            declarations: [SvgEditorComponent]
        })
            .compileComponents();
    });
    beforeEach(() => {
        fixture = TestBed.createComponent(SvgEditorComponent);
        component = fixture.componentInstance;
        fixture.detectChanges();
    });
    it('should create', () => {
        expect(component).toBeTruthy();
    });
    it('should create SVG element based on input params', () => {
        const _svgString = '<svg height="30" width="200"><text x="0" y="15" fill="red">Sample SVG String</text></svg>';
        component.svgContent = _svgString;
        fixture.detectChanges();
        const svgElement = document.getElementById('templateSvg');
        expect(svgElement).toBeTruthy();
        expect(svgElement === null || svgElement === void 0 ? void 0 : svgElement.innerHTML).toBe('Sample SVG String');
    });
    it('should draw edit icon for given SVG file', () => {
        const _svgString = '<svg width="100%" height="100%" xmlns="http://www.w3.org/2000/svg" xmlns:svg="http://www.w3.org/2000/svg" xmlns:se="http://svg-edit.googlecode.com" xmlns:xlink="http://www.w3.org/1999/xlink" viewBox="0 0 841.9 595.3" style="background:#fff"><text x="0" y="15" fill="red">Sample SVG String</text></svg>';
        component.svgContent = DomSanitizer.bypassSecurityTrustHtml(_svgString);
        fixture.detectChanges();
        const svgElement = document.getElementById('templateSvg');
        component.generateMaskForSvgElements();
        console.log('_______ ', document.getElementById('templateSvg')); // TODO: log!
        expect(svgElement).toBeTruthy();
    });
});
